settings.custom_liquid:
{% assign current_category = collection.handle %}

<style>
    .subcategories-grid {

    display: flex;

    flex-wrap: wrap;

    justify-content: center;

    gap: 40px;

    }

    #vendorsDirectory {
    flex: 4;
    display: flex;
    flex-direction: column;
    flex-wrap: wrap;
    gap: 8px; 
    }

    #vendorsDirectory > li {
    flex: 1 1 100px;
    margin-top: 20px;
    list-style: none;
    }

    #vendorsDirectory > li > a {
    display: block;
    font-weight: bold;
    color: #f05a28;
    margin-bottom: 10px;
    text-decoration: none;
    font-size: 24px;
    padding-bottom: 5px;
    border-bottom: 1px solid #ddd;
    }

    #vendorsDirectory > li > ul {
    display: flex;
    flex-wrap: wrap;
    gap: 12px;
    }

    #vendorsDirectory > li > ul > li {
    display: grid;
    grid-template-columns: repeat(auto-fill, minmax(180px, 1fr));
    gap: 20px;
    list-style: none;
    padding: 0;
    margin: 0;
    }

    #vendorsDirectory > li > ul > li > a {
    display: block;
    padding: 4px 0;
    color: #333;
    text-decoration: none;
    font-size: 14px;
    }

    #vendorsDirectory > li > ul > li > a:hover {
    color: #f05a28;
    }
</style>

<div class="subcategories-grid">
    <div id="vendorsDirectory" ></div>
    {% assign vendors_list = shop.vendors %}
    <script>
        var vendorsList = {{ vendors_list | json }};
        var groupedVendors = {};

        vendorsList.forEach(function(vendor) {
        if (vendor !== "") {
            var firstLetter = vendor.charAt(0).toUpperCase();
            if (!groupedVendors[firstLetter]) {
            groupedVendors[firstLetter] = [];
            }
            groupedVendors[firstLetter].push(vendor);
        }
        });
        var output = "";
        for (var letter in groupedVendors) {
        output += "<li><a>" + letter + "</a><ul>";
        groupedVendors[letter].forEach(function(vendor) {
            var encodedVendor = encodeURIComponent(vendor).replace(/'/g, '%27');
            output += "<li><a href='/collections/vendors?q=" + encodedVendor + "'>" + vendor + "</a></li>";
        });
        output += "</ul></li>";
        }
        document.getElementById("vendorsDirectory").innerHTML = output;
    </script>
</div>
