settings.custom_liquid:
{% assign current_category = collection.handle %}

<style>

.subcategories-grid {

  display: flex;

  flex-wrap: wrap;

  justify-content: center;

  gap: 40px;

}

.category-card {
  position: relative;
  flex: 1 1 calc(25% - 20px); /* smaller width per card */
  max-width: 200px;           /* smaller max width */
  aspect-ratio: 1 / 1;
  background-repeat: no-repeat;
  background-size: cover;
  background-position: center;
  overflow: hidden;
  box-shadow: 0 4px 8px rgba(0,0,0,0.1);
  transition: transform 0.3s;
}


.category-card a {

  display: block;

  width: 100%;

  height: 100%;

  position: relative;

  text-decoration: none;

}

.category-card h3 {

  position: absolute;

  bottom: 0;

  left: 0;

  margin: 0;

  width: 100%;

  padding: 10px;

  background: rgba(255, 87, 34, 0.9);

  color: #fff;

  font-size: 16px;

  text-align: center;

  text-transform: uppercase;

}

.category-card:hover {

  transform: translateY(-5px);

}

@media (max-width: 1024px) {

  .category-card {

    flex: 1 1 calc(30% - 10px);

  }

}

@media (max-width: 640px) {

  .category-card {

    flex: 1 1 100%;

  }

}
</style>

{% assign fallback_images = 

  "garden:https://cdn.shopify.com/s/files/1/abc/garden.jpg,

   tools:https://cdn.shopify.com/s/files/1/abc/tools.jpg,

   bathroom:https://cdn.shopify.com/s/files/1/abc/bathroom.jpg,

   electrical:https://cdn.shopify.com/s/files/1/abc/electrical.jpg" | split: "," %}

<div class="subcategories-grid">

    {% for subcollection in collections %}

        {% if subcollection.metafields.custom.parent_category == current_category %}

            {% assign has_products = false %}
            {% if subcollection.products_count > 0 %}
                {% assign has_products = true %}
            {% endif %}

            {%- unless has_products -%}
                {%- for grandchild in collections -%}
                    {% if grandchild.metafields.custom.parent_category == subcollection.handle and grandchild.products_count > 0 %}
                        {% assign has_products = true %}
                    {% endif %}
                {%- endfor -%}
            {%- endunless -%}

            {% if has_products %}

                {% assign image_url = '' %}

                {% if subcollection.image %}
                    {% assign image_url = subcollection.image | img_url: 'medium' %}
                {% else %}
                    {% assign handle_parts = subcollection.handle | split: '-' %}
                    {% assign fallback_found = false %}
                    {% for pair in fallback_images %}
                    {% assign parts = pair | split: ':' %}
                    {% assign keyword = parts[0] | strip %}
                    {% assign fallback_url = parts[1] | strip %}
                    {% if fallback_found == false %}
                        {% for part in handle_parts %}
                            {% if part == keyword %}
                                {% assign image_url = fallback_url %}
                                {% assign fallback_found = true %}
                            {% endif %}
                        {% endfor %}
                    {% endif %}

                    {% endfor %}
                {% endif %}

                <div class="category-card" style="background-image: url('{{ image_url }}');">
                    <a href="{{ subcollection.url }}">
                        <h3>{{ subcollection.title }}</h3>
                    </a>
                </div>
            {% endif %}
        {% endif %}
    {% endfor %}
</div>
